import { Component, HostListener, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../services/auth.service';
import { TranslateService } from '@ngx-translate/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { InfosService } from '../services/infos.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent implements OnInit {
  isDesktop: boolean = window.innerWidth >= 768; // Détecte si l'écran est de type desktop
  feedbackForm!: FormGroup;
  currentRoute!: string;
  isLanguageOverlayVisible: boolean = false;
  isFeedbackVisible: boolean = false; 
  selectedFile!: File;
  isSaturday: boolean;

  constructor(    
    private fb: FormBuilder,
    private router: Router,   
    private infosService: InfosService,
    private authService: AuthService, 
    private translateService: TranslateService
  ) {
    this.isSaturday = new Date().getDay() === 6; // Initialise isSaturday ici
  }

  ngOnInit() {
    this.updateScreenSize(); // Met à jour l'état initial de l'écran
  }

  @HostListener('window:resize', ['$event'])
  onResize(event: any) {
    this.updateScreenSize(); // Met à jour la variable à chaque redimensionnement de l'écran
  }

  updateScreenSize() {
    this.isDesktop = window.innerWidth >= 768;
  }

  shouldDisplayButtons(): boolean {
    const currentRoute = this.router.url;
    return !currentRoute.startsWith('/login');
  }

  shouldDisplayButtonsToAdmin(): boolean {
    const currentRoute = this.router.url;
    return currentRoute.startsWith('/client');
  }

  handleSelectChange(event: Event) {
    const target = event.target as HTMLSelectElement;
    const value = target.value;
    if (value === 'language') {
      this.toggleLangage();
    } else if (value === 'feedback') {
      this.toggleFeedback();
    }
    target.value = '';
  }

  toggleLangage() {
    this.isLanguageOverlayVisible = !this.isLanguageOverlayVisible;
  }

  toggleFeedback() {
    this.isFeedbackVisible = !this.isFeedbackVisible;
  }

  logout() { 
    this.authService.logout(); 
  }

  redirect() {
    const userRole = this.authService.getUserRole();

    if (userRole === 1 || userRole === 2 || userRole === 3 || userRole === 4) {
      this.router.navigate(['/']);
    } else if (userRole === 10 || userRole === 11 || userRole === 12) {
      this.router.navigate(['/client']);
    }
  }

  changeLanguage(lang: string) {
    this.translateService.use(lang); 
    this.toggleLangage();
  }

  handleFileInput(event: any) {
    this.selectedFile = event.target.files[0];
    console.log(this.selectedFile);
  }

  onSubmit(): void {
    if (this.feedbackForm.valid) {
      const formData = new FormData();

      formData.append('email', this.feedbackForm.value['email']);
      formData.append('subject', this.feedbackForm.value['subject']);
      formData.append('description', this.feedbackForm.value['description']);

      if (this.selectedFile) {
        formData.append('file', this.selectedFile);
      }

      this.infosService.sendFeedback(formData).subscribe(
        response => {
          this.isFeedbackVisible = false;
          this.feedbackForm.reset();
        },
        error => {
          console.error('Error:', error);
        }
      );
    }
  }
}
<header class="header-container flex justify-between items-center p-4 bg-white shadow-md">
    <!-- Bouton de déconnexion -->
    <div *ngIf="shouldDisplayButtons()">
        <img (click)="logout()" src="assets/icons/logout.png" alt="logout" class="header-logout"/>
    </div>

    <!-- Logo centré et responsive -->
<div class="flex justify-center items-center w-full">
    <img src="assets/images/logo-j.png" alt="Logo Jeumont" class="header-logo max-h-12 md:max-h-16 object-contain" (click)="redirect()" />
</div>


    <!-- Section pour les boutons ou le combo box -->
    <div *ngIf="shouldDisplayButtons()" class="header-buttons flex items-center">
        <!-- Afficher les boutons séparés uniquement sur les écrans de type desktop -->
        <div class="header-buttons-admin hidden md:flex space-x-2" *ngIf="isDesktop">
            <button (click)="toggleLangage()" class="mr-2 header-buttons-item">
                <img src="assets/icons/langues.png" alt="logo" class="header-buttons-item-logo" />
                {{ 'languageChange' | translate }}
            </button>
            <button (click)="toggleFeedback()" class="header-buttons-item">
                <img src="assets/icons/feedback.png" alt="logo" class="header-buttons-item-logo" />
                Feedback
            </button>
        </div>


    </div>    
</header>


